mapper.select:
SELECT * FROM `users` where 1=1

    AND `nickName` LIKE concat ('%',#{nickName},'%')


    AND `userPhone` =#{userPhone}


    AND `isDeposit`=#{isDeposit}


    AND `isFlag`=#{isFlag}

SELECT * FROM `users` where 1=1

    AND `nickName` LIKE concat ('%',#{nickName},'%')


    AND `userPhone` =#{userPhone}


    AND `isDeposit`=#{isDeposit}


    AND `isFlag`=#{isFlag}

limit #{pageNo},#{pageSize}

SELECT count(*) FROM `users` where 1=1

    AND `nickName` LIKE concat ('%',#{nickName},'%')


    AND `userPhone` =#{userPhone}


    AND `isDeposit`=#{isDeposit}


    AND `isFlag`=#{isFlag}
